mapper.insert:

		SELECT approval_id_seq.CURRVAL
		FROM dual					

	INSERT INTO approval_path
            (
                approval_id
                , approval_type
                , approval_order
                , approval_emp_order
                , approval_emp
                , approval_emp_id
                , doc_id
                , draft_emp
            )
	        VALUES
	        (
	        	approval_id_seq.NEXTVAL
	        	, #{approvalType}
	        	, #{approvalOrder}
	        	, #{approvalEmpOrder}
	        	, #{approvalEmp}
	        	, #{approvalEmpId}
	        	, (SELECT last_number-1
				  FROM user_sequences 
				  WHERE sequence_name=UPPER('doc_id_seq'))
	        	, #{draftEmp}
	        )

mapper.update:
UPDATE approval_path
SET approval_type = '결재대기'
WHERE approval_emp_id = #{approvalEmpId}
AND   doc_id = #{docId}

mapper.delete:
DELETE FROM approval_path
WHERE approval_emp_id = #{approvalEmpId}
AND doc_id = #{docId}

