mapper.insert:

	SELECT draft_id_seq.CURRVAL
	FROM dual					

INSERT INTO draft
	(
		draft_id
		, draft_emp
		, draft_emp_dept
		, draft_title
		, draft_content
		, draft_date				
	)
VALUES
	(
		draft_id_seq.NEXTVAL
		, #{draftEmp}
		, #{draftEmpDept}
		, #{draftTitle}
		, #{draftContent}
		, #{draftDate}				
	)
