mapper.update:
UPDATE calendar_box
SET color = #{color}	
WHERE calendar_box_id = #{calendarBoxId}

UPDATE calendar_box
SET expression_yn = #{expressionYn}
WHERE calendar_box_id = #{calendarBoxId}

        UPDATE shared_cal
		SET expression_yn = #{expressionYn}
		WHERE calendar_box_id = #{calendarBoxId} AND emp_id = 100

UPDATE shared_cal
SET color = #{color}
WHERE calendar_box_id = #{calendarBoxId} AND emp_id = 100

mapper.insert:

	SELECT NVL(MAX(calendar_id),1) + 1
	FROM calendar

	insert into calendar
    (
        calendar_id
        , start_date
        , end_date
        , title
        , content
        , emp_id
        , calendar_box_id
        , location
    )
VALUES (
         #{calendarId}
         , TO_DATE(#{start}, 'yyyy/MM/dd HH24:MI')
         , TO_DATE(#{end}, 'yyyy/MM/dd HH24:MI')
         , #{title}
         , #{content}
         , 100
         , #{calendarBoxId}
         , #{location}
        )

mapper.update:
UPDATE calendar
SET 
		start_date = TO_DATE(#{start}, 'yyyy/MM/dd HH24:MI:ss')
		, end_date = TO_DATE(#{end}, 'yyyy/MM/dd HH24:MI:ss')
		, title = #{title}
		, content = #{content}
		, calendar_box_id = #{calendarBoxId}
		, location = #{location}
WHERE calendar_id = #{calendarId}
