	, upload_path
	, ext
	, file_size
	, emp_id
	, type
)
VALUES(
	#{fileId}
	, #{attachmentGroupId}
	, #{fileType}
	, #{originFileName}
	, #{uploadFileName}
	, #{uploadPath}
	, #{ext}
	, #{fileSize}
	, #{empId}
	, #{type}
)

