mapper.insert:

INSERT ALL
    INTO emp
    	(
    	emp_id
    	, emp_name
    	, tel
    	, mail
    	, hire_date
    	, address
    	, photo
    	, department_id
    	, emp_position
    	)
    VALUES
        (
            #{empId}
            , #{empName}
            , #{tel}
            , #{mail}
            , #{hireDate}
            , #{address}
            , #{photo}
			, FIND_CODE(#{departmentName})
		    , FIND_CODE(#{posName})
        )
    INTO account
    (account_id, password, emp_id)
    VALUES
        (
        	#{accountId}
        	,#{password}
            , #{empId}
        )
SELECT * FROM dual

