mapper.select:
SELECT emp_id
FROM emp
WHERE mail = #{mail}

SELECT mail
FROM emp
WHERE emp_id = #{empId}

mapper.insert:
INSERT INTO mail
	       (
	       mail_id
	       , sender
	       , recipient
	       , cc
	       , title
	       , content
	       , mailbox_id
	       , emp_id
	       )
	VALUES
	       (
	           (SELECT NVL(MAX(mail_id),0)+1 mail_id
	           FROM mail)
	           , #{sender}
	           , #{recipient}
	           , #{cc}
	           , #{title}
	           , #{content}
	           , 'd2'
	           , #{empId}
			)

