mapper.select:
SELECT * FROM( SELECT rownum AS rs, a.* FROM ( SELECT post_id, board_id, notification_yn, hold_yn, title, write_date, view_cnt, recommend_cnt, attachment_group_id, content, emp_id, emp_id_name(emp_id) "empName" FROM post WHERE board_id = 1 AND (notification_yn = 'Y')
ORDER BY view_cnt DESC
ORDER BY recommend_cnt DESC
ORDER BY post_id DESC
)a WHERE rownum <= #{page} * #{cnt} ) WHERE rs > (#{page}-1) * #{cnt}

SELECT COUNT(*) 
FROM post 
WHERE board_id = #{boardId}

mapper.update:
	UPDATE post
    SET view_cnt = view_cnt + 1
    WHERE post_id = #{postId}

	UPDATE post
    SET recommend_cnt = recommend_cnt + 1
    WHERE post_id = #{postId}
    AND emp_id = #{empId}

	UPDATE post
    SET recommend_cnt = recommend_cnt - 1
    WHERE post_id = #{postId}
