mapper.select:
SELECT * FROM( SELECT rownum AS rs, a.* FROM ( SELECT post_id, board_id, notification_yn, hold_yn, title, write_date, view_cnt, recommend_cnt, attachment_group_id, content, e.emp_id, emp_id_name(e.emp_id) emp_name FROM post p JOIN emp e ON p.emp_id = e.emp_id WHERE board_id = 2 AND e.department_id = #{searchVO.departmentId}
AND title like '%'||#{searchVO.keyword}||'%'
AND content like '%'||#{searchVO.keyword}||'%'
AND emp_id_name(e.emp_id) like '%'||#{searchVO.keyword}||'%'
AND title like '%'||#{searchVO.keyword}||'%' OR content like '%'||#{searchVO.keyword}||'%'
ORDER BY view_cnt DESC
ORDER BY recommend_cnt DESC
ORDER BY post_id DESC
)a WHERE rownum <= #{postVO.page} * #{postVO.cnt} ) WHERE rs > (#{postVO.page}-1) * #{postVO.cnt}

mapper.update:
UPDATE post 
	SET 
		hold_yn = #{holdYn},
		title = #{title},
		content = #{content}	
	WHERE post_id = #{postId}

	UPDATE post
    SET view_cnt = view_cnt + 1
    WHERE post_id = #{postId}

	UPDATE post
    SET recommend_cnt = recommend_cnt + 1
    WHERE post_id = #{postId}

	UPDATE post
    SET recommend_cnt = recommend_cnt - 1
    WHERE post_id = #{postId}
